OscillateNum_Baselines:	
		dc.w  $7D					; direction bitfield
		
		; start value, start rate
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
		dc.w $80, 0
