		jsrto	LoadPLC,JmpTo3_AddPLC
		moveq	#0,d0
		move.b	(v_zone).w,d0
		move.b	Animal_PLCs(pc,d0.w),d0			; load the animal graphics for the current zone
		jsrto	LoadPLC,JmpTo3_AddPLC

