; ===========================================================================

SegaHideTM_Init:				
		bsr.w	LoadSubtypeData
		move.b	#id_Frame_HideTM,ost_frame(a0)
		move.w	#screen_left+244,ost_x_screen(a0)
		move.w	#screen_top+88,ost_y_screen(a0)
		rts	
