plcm:		macro gfx,vram,suffix
		dc.l gfx
		ifarg \vram
			plcm_vram: = \vram
		else
			plcm_vram: = last_vram
		endc
		last_vram: = plcm_vram+sizeof_\gfx
		dc.w plcm_vram
		ifarg \suffix
			tile_\gfx\_\suffix: equ plcm_vram/sizeof_cell
		else
			if ~def(tile_\gfx)
			tile_\gfx: equ plcm_vram/sizeof_cell
			endc
		endc
		endm


plcheader:	macro *
		\*: equ *
		plc_count\@: equ (\*_end-\*-2)/sizeof_plc
		dc.w plc_count\@-1
		endm		
		
